mapper.select:
select p_id, p_name, p_price, p_stock, p_category_name, p_description
from products
where p_id=#{p_id}


select rnum, p_name,p_price, photo_sname, photo_type
  from (
      select rownum as rnum, p_name,p_price, photo_sname, photo_type
      from (
           select p.p_name,p.p_price, ph.photo_sname,ph.photo_type 
          from products p Join photos ph
          on p.p_id = ph.p_id and
          ph.photo_role='main'
          order by p.p_upload_date desc
      )
      where rownum <= #{endRowNo}
  )
  where rnum >= #{startRowNo}


select rnum, p_name,p_price, photo_sname, photo_type
  from (
      select rownum as rnum, p_name,p_price, photo_sname, photo_type
      from (
           select p.p_name,p.p_price, ph.photo_sname,ph.photo_type 
          from products p Join photos ph
          on p.p_id = ph.p_id and
          ph.photo_role='main'
      )
      where rownum <= #{endRowNo}
  )
  where rnum >= #{startRowNo}

select count(*)
from products
where p_category_name=#{p_category_name}

select count(*)
from products
where p_category_name=#{p_category_name}

mapper.update:
update products set p_id=#{p_id}, p_name=#{p_name}, p_price=#{p_price}, p_stock=#{p_stock}, p_salescount=#{p_salescount}, p_category_name=#{p_category_name},
p_upload_date=#{p_upload_date}, p_description=#{p_description}
where p_id=#{p_id}
